import * as Knex from 'knex';

export async function seed(knex: Knex): Promise<void> {
    const areas: any[] = [
        {
            deliveryFee: 13,
            name: 'Vila Mariana',
            polygon:
            // eslint-disable-next-line max-len
                ''
        },
        {
            deliveryFee: 18,
            name: 'Sorocaba',
            polygon:
            // eslint-disable-next-line max-len
                ''
        }
    ];

    const dbAreas = await knex
        .count()
        .from('area')
        .first();

    if (Number(dbAreas.count) > 0) return;

    const query = knex
        .insert(areas)
        .into('area')
        .toString();

    await knex.raw(query);
}
